# -*- coding: utf-8 -*-

'''
最长回文子串

动态规划
'''

class Solution(object):
    def longestPalindrome(self, s):
        if s == '':
            return ''
        length = len(s)

        import numpy as np
        maxi = 0
        maxj = 0
        dp = np.zeros((length, length))
        for i in range(length):
            dp[i, i] = 1
            if(i < length - 1):
                if(s[i] == s[i+1]):
                    dp[i][i+1] = 1
                    maxi = i
                    maxj = i+1

        for l in range(3, length + 1):
            for i in range(length+1-l):
                j = i + l -1
                if(s[i] == s[j] and dp[i+1, j-1]):
                    dp[i][j] = 1
                    maxi = i
                    maxj = j
        return s[maxi:maxj + 1]

